'use strict';

const src = './html/';
const fs = require('fs');

var obj = {};

module.exports = function(){

  fs.readdir(src, (err, files) => {
    files.forEach((file, index) => {
      obj[index] = file;

      fs.writeFile('./guide_src/data/file_list.json', JSON.stringify(obj), function(err) {
        if(err) throw err;
        console.log('File write completed');
      });
    });
  });

};
